const initialState = true;

export const isSidebarOpen = (state = initialState, action) => {
    switch (action.type) {
        case 'TOGGLE_SIDEBAR':
            const temp = !state;
            return temp;
        default:
            return state;
    }

}

export default isSidebarOpen;
