//     // </Switch>;

//     return (
//       <Router>
//         <div className="App">
//           <Appbar />
//           <Sidebar />
//         </div>
//       </Router>
//     );
//   }
}

const mapStateToProps = (state) => {
  return {
    isLoggedIn: state.isLoggedIn,
    username: state.username,
  };
};

export default connect(mapStateToProps)(App);
